class MainActivity : AppCompatActivity() {
    private val CAMERA_PERMISSION_REQUEST = 123

    @RequiresApi(Build.VERSION_CODES.O)
    override fun onCreate(savedInstanceState: Bundle?) {
        super.onCreate(savedInstanceState)
        setContentView(R.layout.activity_main)


        val openCameraButton = findViewById<Button>(R.id.openCameraButton)
        openCameraButton.setOnClickListener {
            if (ContextCompat.checkSelfPermission(this, Manifest.permission.CAMERA)
                == PackageManager.PERMISSION_GRANTED
            ) {
                openScanner()
            } else {
                ActivityCompat.requestPermissions(
                    this,
                    arrayOf(Manifest.permission.CAMERA),
                    CAMERA_PERMISSION_REQUEST
                )
            }
        }

        val generateQRButton = findViewById<Button>(R.id.generateQRButton)
        generateQRButton.setOnClickListener {
            val i = Intent(this@MainActivity, SelectResources::class.java)
            startActivity(i)
        }

